local PANEL = {}local accent_col = Color(0, 195, 165)local accent_col_dark = Color(0, 125, 95)local col_red = Color(200, 0, 0)local col_green = Color(0, 200, 0)function PANEL:Init()    self.Col = Color(16, 16, 16)    self:Dock(FILL)    self.Paint = function(this, w, h)        draw.RoundedBoxEx(16, 0, 0, w, h, Color(6, 6, 6), false, false, false, true)    end    -- Create the panel for daily rewards    self.DailyRewardsPanel = vgui.Create("DPanel", self)    self.DailyRewardsPanel:Dock(FILL)    self.DailyRewardsPanel:DockMargin(5, 5, 5, 5)    self.DailyRewardsPanel.Paint = function(this, w, h)        -- Customize the appearance of the panel if needed        draw.RoundedBox(8, 0, 0, w, h, Color(50, 50, 50))    end    net.Start("luctus_dayward_sync")    net.SendToServer()    -- Explanation label    local explanation = vgui.Create("DLabel", self.DailyRewardsPanel)    explanation:SetText("Put "..LUCTUS_DAYWARD_NAME_TAG.." in front of your name to get "..LUCTUS_DAYWARD_NAME_MULTIPLIER.."x rewards!")    explanation:SetFont("DermaLarge")    explanation:SetTextColor(Color(255, 255, 255))    explanation:SetPos(ScrW() * 0.1, ScrH() * 0.05)    explanation:SizeToContents()    -- Status label    local statusPanel = vgui.Create("DLabel", self.DailyRewardsPanel)    statusPanel:SetText(LocalPlayer():Name().." has better rewards: " .. (string.StartWith(LocalPlayer():SteamName(), LUCTUS_DAYWARD_NAME_TAG) and "Yes" or "No"))    statusPanel:SetFont("DermaLarge")    statusPanel:SetTextColor(string.StartWith(LocalPlayer():SteamName(), LUCTUS_DAYWARD_NAME_TAG) and col_green or col_red)    statusPanel:SetPos(ScrW() * 0.1, ScrH() * 0.15)    statusPanel:SizeToContents()    -- Daily rewards list    local RewardsList = vgui.Create("DPanelList", self.DailyRewardsPanel)    RewardsList:SetSize(ScrW() * 0.7, ScrH() * 0.7)    RewardsList:SetPos(ScrW() * 0.1, ScrH() * 0.2)    RewardsList:EnableVerticalScrollbar(true)    RewardsList:EnableHorizontal(false)    RewardsList:SetSpacing(5)    -- Populate rewards list    for k, v in ipairs(LUCTUS_DAYWARD_AMOUNT) do        local panel = vgui.Create("DPanel")        panel:SetSize(ScrW() * 0.7, 30)        function panel:Paint(w, h)            draw.RoundedBox(0, 0, 0, w, h, Color(44, 47, 52))        end        local labelText = ""        for key, amount in pairs(v) do            if labelText ~= "" then labelText = labelText .. ", " end            if LUCTUS_DAYWARD_TYPES[key] then                labelText = labelText .. amount .. LUCTUS_DAYWARD_TYPES[key][1]            end        end        local rewardLabel = vgui.Create("DLabel", panel)        rewardLabel:SetText(labelText)        rewardLabel:SetFont("DermaLarge")        rewardLabel:SetTextColor(Color(255, 255, 255))        rewardLabel:SetPos(ScrW() * 0.1, 5)        rewardLabel:SizeToContents()        RewardsList:AddItem(panel)    end    -- Create a single big claim button at the bottom	local claimAllButton = vgui.Create("DButton", self.DailyRewardsPanel)	claimAllButton:SetText("Claim All")	claimAllButton:SetTextColor(Color(255, 255, 255))	claimAllButton:SetPos(0, ScrH() * 0.8) -- Adjusted position to start from the left	claimAllButton:Dock(BOTTOM) -- Dock the button to the bottom of the panel	claimAllButton:DockMargin(5, 5, 5, 5) -- Added margin for better spacing	claimAllButton:SetTall(60) -- Increased the height of the button	claimAllButton.DoClick = function()		net.Start("luctus_claim_all_rewards")		net.SendToServer()	end	claimAllButton.Paint = function(self, w, h)		draw.RoundedBox(16, 0, 0, w, h, Color(100, 100, 255)) -- Changed color to a more suitable one and added curved corners	endend-- Register your custom panel class with VGUIvgui.Register("DailyReward", PANEL)