local PANEL = {}local accent_col = Color(0, 195, 165)local accent_col_dark = Color(0, 125, 95)local col_red = Color(200, 0, 0)local col_green = Color(0, 200, 0)function PANEL:Init()    self.Col = Color(16, 16, 16)    self:Dock(FILL)    self.Paint = function(this, w, h)        draw.RoundedBoxEx(16, 0, 0, w, h, Color(6, 6, 6), false, false, false, true)    end    -- Create the panel for daily rewards    self.DailyRewardsPanel = vgui.Create("DPanel", self)    self.DailyRewardsPanel:Dock(TOP)    self.DailyRewardsPanel:DockMargin(5, 5, 5, 5)    self.DailyRewardsPanel:SetTall(300) -- Adjust the height of the panel    self.DailyRewardsPanel:Dock(FILL) -- Fill the available space    self.DailyRewardsPanel.Paint = function(this, w, h)        -- Customize the appearance of the panel if needed        draw.RoundedBox(8, 0, 0, w, h, Color(50, 50, 50))    end    -- Title label    local titleLabel = vgui.Create("DLabel", self.DailyRewardsPanel)    titleLabel:SetText("Daily Rewards")    titleLabel:SetFont("DermaLarge")    titleLabel:SetTextColor(Color(255, 255, 255))    titleLabel:Dock(TOP)    titleLabel:DockMargin(0, 10, 0, 10)    titleLabel:SetContentAlignment(5)	    -- Explanation label    local explanation = vgui.Create("DLabel", self.DailyRewardsPanel)    explanation:SetText("Put "..LUCTUS_DAYWARD_NAME_TAG.." in front of your name to get "..LUCTUS_DAYWARD_NAME_MULTIPLIER.."x rewards!")    explanation:SetFont("DermaLarge")    explanation:SetTextColor(Color(255, 255, 255))    explanation:Dock(TOP)    explanation:DockMargin(10, 10, 10, 20)    explanation:SetContentAlignment(5)	-- Create a panel to contain the buttons	local buttonContainer = vgui.Create("DPanel", self.DailyRewardsPanel)	buttonContainer:Dock(TOP)	buttonContainer:SetTall(50)  -- Adjust the height as needed	buttonContainer:DockMargin(10, 10, 10, 0)  -- Add margin to the top	-- Set the background color of the container to be fully transparent	buttonContainer:SetBackgroundColor(Color(0, 0, 0, 0))	-- Create the buttons	local button1 = vgui.Create("DButton", buttonContainer)	local button2 = vgui.Create("DButton", buttonContainer)	local button3 = vgui.Create("DButton", buttonContainer)	-- Set the text for each button	button1:SetText("Gift 1")	button2:SetText("Gift 2")	button3:SetText("Gift 3")	-- Dock the buttons horizontally within the button container	button1:Dock(LEFT)	button2:Dock(LEFT)	button3:Dock(LEFT)	-- Apply padding between the buttons if needed	local padding = 10	button1:DockMargin(0, 0, padding, 0)	button2:DockMargin(0, 0, padding, 0)	-- Optionally, you can adjust the width of each button to make them evenly spaced	local buttonWidth = (buttonContainer:GetWide() * padding) / 3	button1:SetWide(buttonWidth)	button2:SetWide(buttonWidth)	button3:SetWide(buttonWidth)	print(buttonContainer:GetWide())    -- Randomize button    local randomButton = vgui.Create("DButton", self.DailyRewardsPanel)    randomButton:SetText("Random")    randomButton:SetTextColor(Color(255, 255, 255))    randomButton:Dock(BOTTOM)    randomButton:DockMargin(10, 10, 10, 10)    randomButton.Paint = function(self, w, h)        draw.RoundedBox(8, 0, 0, w, h, accent_col)    end    randomButton.DoClick = function()        local randomIndex = math.random(1, moneyBoxCount)        local moneyLabel = moneyContainer:GetChildren()[randomIndex]:GetChildren()[1]        local randomAmount = math.random(100, 1000)        moneyLabel:SetText("$"..randomAmount)    endend-- Register your custom panel class with VGUIvgui.Register("DailyReward", PANEL)