local PANEL = {}function PANEL:Init()    self.Col = Color(16, 16, 16)    self:Dock(FILL)    self.Paint = function(this, w, h)        draw.RoundedBoxEx(16, 0, 0, w, h, Color(6, 6, 6), false, false, false, true)    end    -- Create the panel for daily rewards    self.DailyRewardsPanel = vgui.Create("DPanel", self)    self.DailyRewardsPanel:Dock(FILL)    self.DailyRewardsPanel:DockMargin(5, 5, 5, 5)    self.DailyRewardsPanel.Paint = function(this, w, h)        -- Customize the appearance of the panel if needed        draw.RoundedBox(8, 0, 0, w, h, Color(50, 50, 50))    end    -- Add UI elements for daily rewards, such as buttons, labels, etc.    self.DailyRewardsLabel = vgui.Create("DLabel", self.DailyRewardsPanel)    self.DailyRewardsLabel:SetText("Daily Rewards")    self.DailyRewardsLabel:SetFont("DermaLarge")    self.DailyRewardsLabel:SetTextColor(Color(255, 255, 255))    self.DailyRewardsLabel:Dock(TOP)    self.DailyRewardsLabel:DockMargin(10, 10, 10, 10)    self.DailyRewardsLabel:SetContentAlignment(5)    self.DailyRewardsButton = vgui.Create("DButton", self.DailyRewardsPanel)    self.DailyRewardsButton:SetText("Claim Daily Reward")    self.DailyRewardsButton:SetTextColor(Color(255, 255, 255))    self.DailyRewardsButton:Dock(BOTTOM)    self.DailyRewardsButton:DockMargin(10, 10, 10, 10)    self.DailyRewardsButton:SetTall(40)    self.DailyRewardsButton.Paint = function(this, w, h)        local col = Color(66, 139, 202)  -- Customize the button color if needed        local hovering = this:IsHovered()        if hovering then            col = Color(46, 116, 191)  -- Change color when hovering        end        draw.RoundedBox(8, 0, 0, w, h, col)    end    self.DailyRewardsButton.DoClick = function()        -- Handle the click event to claim daily rewards        print("Daily rewards claimed!")        -- Implement your logic here    endend-- Register your custom panel class with VGUIvgui.Register("DailyReward", PANEL)