local PANEL = {}local accent_col = Color(0, 195, 165)local dark_accent_col = Color(44, 47, 52)local hover_accent_col = Color(66, 70, 77)function PANEL:Init()    self.Col = Color(16, 16, 16)    self:Dock(FILL)    -- Create the panel for skills system    self.SkillsPanel = vgui.Create("DPanel", self)    self.SkillsPanel:Dock(FILL)    self.SkillsPanel.Paint = function(this, w, h)        -- Customize the appearance of the panel if needed        draw.RoundedBox(0, 0, 0, w, h, Color(50, 50, 50))    end		RunConsoleCommand("skills")    -- Add UI elements for skills system, such as buttons, labels, etc.    self.SkillsLabel = vgui.Create("DLabel", self.SkillsPanel)    self.SkillsLabel:SetText("Skills")    self.SkillsLabel:SetFont("DermaLarge")    self.SkillsLabel:SetTextColor(accent_col)    self.SkillsLabel:Dock(TOP)    self.SkillsLabel:DockMargin(10, 10, 10, 10)    self.SkillsLabel:SetContentAlignment(5)    -- Display points left    local pointsLeft = vgui.Create("DPanel", self.SkillsPanel)    pointsLeft:Dock(TOP)    pointsLeft:SetHeight(30)    pointsLeft.Paint = function(self, w, h)        draw.RoundedBox(0, 0, 0, w, h, Color(54, 57, 62))        draw.SimpleText("Points left: "..LUCTUS_SKILLS_FREEPOINTS, "Trebuchet18", w/2, h/2, accent_col, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER)    end    -- Create a grid layout for the skills buttons    local skillGrid = vgui.Create("DGrid", self.SkillsPanel)    skillGrid:Dock(FILL)    skillGrid:SetCols(5) -- Set 5 columns for each row    skillGrid:SetColWide(300) -- Set width for each button    skillGrid:SetRowHeight(200) -- Set height for each button    -- Panel to display skill name and description    self.SkillInfoPanel = vgui.Create("DPanel", self.SkillsPanel)    self.SkillInfoPanel:Dock(BOTTOM)    self.SkillInfoPanel:SetTall(250) -- Increase height for more space    self.SkillInfoPanel:DockMargin(10, 10, 10, 10)    self.SkillInfoPanel.Paint = function(self, w, h)        draw.RoundedBox(8, 0, 0, w, h, Color(50,50,50)) -- Change the color of the background here    end    -- Label to display skill name    self.SkillNameLabel = vgui.Create("DLabel", self.SkillInfoPanel)    self.SkillNameLabel:SetText("")    self.SkillNameLabel:SetFont("DermaLarge")    self.SkillNameLabel:SetTextColor(accent_col)    self.SkillNameLabel:Dock(TOP)    self.SkillNameLabel:DockMargin(10, 10, 10, 10)    self.SkillNameLabel:SetContentAlignment(5)	    -- Label to display skill description    self.SkillDescLabel = vgui.Create("DLabel", self.SkillInfoPanel)    self.SkillDescLabel:SetText("")    self.SkillDescLabel:SetFont("DermaDefault")    self.SkillDescLabel:SetTextColor(accent_col)    self.SkillDescLabel:Dock(FILL)    self.SkillDescLabel:DockMargin(10, 0, 10, 0)    self.SkillDescLabel:SetWrap(true)    self.SkillDescLabel:SetAutoStretchVertical(true)    -- Button to level down the skill in the skill info panel    self.LevelDownSkillButton = vgui.Create("DButton", self.SkillInfoPanel)    self.LevelDownSkillButton:SetText("Level Down Skill")    self.LevelDownSkillButton:SetTextColor(accent_col)    self.LevelDownSkillButton:Dock(BOTTOM)    self.LevelDownSkillButton:SetTall(30)    self.LevelDownSkillButton:DockMargin(10, 10, 10, 10)    self.LevelDownSkillButton.Paint = function(self, w, h)        draw.RoundedBox(0, 0, 0, w, h, dark_accent_col)        if self.Hovered then            draw.RoundedBox(0, 0, 0, w, h, hover_accent_col)        end    end    self.LevelDownSkillButton.DoClick = function()        local skillName = self.SkillNameLabel:GetText()  -- Get the name of the skill        LuctusSkillsChange(skillName, false)  -- Level down the skill    end    -- Button to level up the skill in the skill info panel    self.LevelUpSkillButton = vgui.Create("DButton", self.SkillInfoPanel)    self.LevelUpSkillButton:SetText("Level Up Skill")    self.LevelUpSkillButton:SetTextColor(accent_col)    self.LevelUpSkillButton:Dock(BOTTOM)    self.LevelUpSkillButton:SetTall(30)    self.LevelUpSkillButton:DockMargin(10, 10, 10, 10)    self.LevelUpSkillButton.Paint = function(self, w, h)        draw.RoundedBox(0, 0, 0, w, h, dark_accent_col)        if self.Hovered then            draw.RoundedBox(0, 0, 0, w, h, hover_accent_col)        end    end    self.LevelUpSkillButton.DoClick = function()        local skillName = self.SkillNameLabel:GetText()  -- Get the name of the skill        LuctusSkillsChange(skillName, true)  -- Level up the skill    end    -- Iterate over skills and create buttons dynamically within the grid layout    for skillName, skillData in pairs(LUCTUS_SKILLS) do        local skillButtonPanel = vgui.Create("DPanel", skillGrid)        skillButtonPanel:SetSize(200, 200)        local skillButton = vgui.Create("DButton", skillButtonPanel)        skillButton:SetText(skillName)        skillButton:SetTextColor(accent_col)        skillButton:Dock(FILL)                -- Assign a different color to each button based on its index        skillButton.Paint = function(self, w, h)            draw.RoundedBox(0, 0, 0, w, h, Color(50,50,50))        end        local progressBar = vgui.Create("DPanel", skillButtonPanel)        progressBar:Dock(BOTTOM)        progressBar:SetHeight(20)        progressBar:SetPaintBackground(false)        function progressBar:Paint(w, h)            local width = (LUCTUS_SKILLS_LOCAL[skillName] * w) / skillData.max            draw.RoundedBox(0, 0, 0, w, h, Color(50,50,50))            draw.RoundedBox(0, 0, 0, width, h, accent_col)            draw.SimpleText(LUCTUS_SKILLS_LOCAL[skillName] .. "/" .. skillData.max, "Trebuchet18", w / 2, h / 2, color_white, TEXT_ALIGN_CENTER, TEXT_ALIGN_CENTER)        end        skillButton.DoClick = function()            -- Handle the click event for the skill button            self.SkillInfoPanel:SetVisible(true)            self.SkillNameLabel:SetText(skillName)            self.SkillDescLabel:SetText(skillData.desc)        end        skillGrid:AddItem(skillButtonPanel)    end    local saveButton = vgui.Create("DButton", self.SkillsPanel)    saveButton:SetText("◄ Save Your Skills ►")    saveButton:SetTextColor(Color(0, 0, 0))    saveButton:Dock(BOTTOM)    saveButton:DockMargin(10, 10, 10, 10)    saveButton.Paint = function(self, w, h)        draw.RoundedBox(8, 0, 0, w, h, accent_col)    end    saveButton.DoClick = function()        LuctusSkillsSend()    endend-- Register your custom panel class with VGUIvgui.Register("Skills", PANEL, "DPanel")